import React, { useState } from "react";
import {
  Button,
  LinearProgress,
  Card,
  CardContent,
  Typography,
  TextField,
  Grid
} from "@material-ui/core";

const totalSteps = 12;

const PlanFinder = () => {
  const [step, setStep] = useState(1);
  const [answers, setAnswers] = useState({});
  const [leadInfo, setLeadInfo] = useState({
    name: "",
    email: "",
    company: "",
    phone: ""
  });

  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  const handleAnswer = (key, value) => {
    setAnswers(prev => ({ ...prev, [key]: value }));
  };

  const handleMultiSelect = (key, value) => {
    setAnswers(prev => {
      const existing = prev[key] || [];
      if (existing.includes(value)) {
        return { ...prev, [key]: existing.filter(v => v !== value) };
      }
      return { ...prev, [key]: [...existing, value] };
    });
  };

  const handleLeadChange = (e) => {
    setLeadInfo({ ...leadInfo, [e.target.name]: e.target.value });
  };

  const isStepValid = () => {
    switch (step) {
      case 1: return answers.employeeCount !== undefined;
      case 2: return answers.branches !== undefined;
      case 3: return answers.payrollSystem !== undefined;
      case 4: return answers.workerTypes && answers.workerTypes.length > 0;
      case 5: return answers.featureLevel !== undefined;
      case 6: return answers.reportType !== undefined;
      case 7: return answers.workflow !== undefined;
      case 8: return answers.selfService !== undefined;
      case 9: return answers.supportLevel !== undefined;
      case 11:
        return (
          leadInfo.name &&
          leadInfo.email &&
          leadInfo.company &&
          leadInfo.phone
        );
      default:
        return true;
    }
  };

  const calculateScore = () => {
    let total = 0;

    Object.keys(answers).forEach(key => {
      if (Array.isArray(answers[key])) {
        total += answers[key].reduce((a, b) => a + b, 0);
      } else {
        total += answers[key];
      }
    });

    if (answers.workerTypes && answers.workerTypes.length >= 3) {
      total += 2;
    }

    return total;
  };

  const getRecommendation = (score) => {
    if (score <= 10) return { name: "Starter Plan", price: "2,000 PHP" };
    if (score <= 20) return { name: "Professional Plan", price: "6,000 PHP" };
    return { name: "Enterprise Plan", price: "13,000 PHP" };
  };

  const progress = (step / totalSteps) * 100;

  const OptionButton = ({ selected, onClick, children }) => (
    <Button
      onClick={onClick}
      fullWidth
      style={{
        marginBottom: 18,
        padding: "18px 20px",
        borderRadius: "10px",
        border: "1px solid #e5e7eb",
        backgroundColor: selected ? "#FF7504" : "#ffffff",
        color: selected ? "#ffffff" : "#111827",
        fontWeight: 600,
        fontSize: "15px",
        textTransform: "none",
        boxShadow: selected
          ? "0 6px 16px rgba(255,117,4,0.25)"
          : "none",
        transition: "all 0.2s ease"
      }}
    >
      {children}
    </Button>
  );

  const renderStep = () => {
    switch (step) {

      case 1:
        return (
          <>
            <Typography style={questionStyle}>
              How many employees do you have?
            </Typography>
            <OptionButton selected={answers.employeeCount === 1} onClick={() => handleAnswer("employeeCount", 1)}>1â€“20</OptionButton>
            <OptionButton selected={answers.employeeCount === 2} onClick={() => handleAnswer("employeeCount", 2)}>21â€“50</OptionButton>
            <OptionButton selected={answers.employeeCount === 3} onClick={() => handleAnswer("employeeCount", 3)}>50â€“150</OptionButton>
            <OptionButton selected={answers.employeeCount === 4} onClick={() => handleAnswer("employeeCount", 4)}>151+</OptionButton>
          </>
        );

      case 10:
        const recommendation = getRecommendation(calculateScore());
        return (
          <div style={{ textAlign: "center" }}>
            <Typography style={{ fontSize: 18, marginBottom: 10 }}>
              ðŸŽ‰ Based on your responses, we recommend:
            </Typography>

            <Typography style={{ fontSize: 32, fontWeight: 700, marginBottom: 10 }}>
              {recommendation.name}
            </Typography>

            <Typography style={{ fontSize: 18, marginBottom: 30 }}>
              {recommendation.price} per month
            </Typography>

            <div style={{
              backgroundColor: "#f9fafc",
              padding: 25,
              borderRadius: 12,
              marginBottom: 30
            }}>
              <Typography style={{ marginBottom: 8 }}>
                âœ” Suitable for your company size
              </Typography>
              <Typography style={{ marginBottom: 8 }}>
                âœ” Matches your payroll complexity
              </Typography>
              <Typography>
                âœ” Supports your workflow & reporting needs
              </Typography>
            </div>

            <Button
              onClick={() => setStep(11)}
              style={{
                padding: "12px 32px",
                borderRadius: 8,
                backgroundColor: "#FF7504",
                color: "#fff",
                fontWeight: 600
              }}
            >
              Proceed to Avail
            </Button>
          </div>
        );

      case 11:
        return (
          <>
            <Typography style={questionStyle}>
              Enter your details
            </Typography>

            <TextField label="Full Name" name="name" fullWidth margin="normal" onChange={handleLeadChange} />
            <TextField label="Email" name="email" fullWidth margin="normal" onChange={handleLeadChange} />
            <TextField label="Company" name="company" fullWidth margin="normal" onChange={handleLeadChange} />
            <TextField label="Phone Number" name="phone" fullWidth margin="normal" onChange={handleLeadChange} />

            <Button
              disabled={!isStepValid()}
              onClick={() => setStep(12)}
              style={{
                marginTop: 20,
                padding: "12px 32px",
                borderRadius: 8,
                backgroundColor: "#FF7504",
                color: "#fff",
                fontWeight: 600
              }}
            >
              Submit Inquiry
            </Button>
          </>
        );

      case 12:
        return (
          <div style={{ textAlign: "center", padding: 30 }}>
            <Typography style={{ fontSize: 28, fontWeight: 700, marginBottom: 15 }}>
              âœ… Thank You!
            </Typography>
            <Typography style={{ marginBottom: 10 }}>
              We truly appreciate your time and patience in completing the UlapPayroll Plan Finder.
            </Typography>
            <Typography style={{ marginBottom: 25 }}>
              Our team will contact you shortly.
            </Typography>

            <Button
              variant="outlined"
              onClick={() => {
                setStep(1);
                setAnswers({});
                setLeadInfo({ name: "", email: "", company: "", phone: "" });
              }}
            >
              Start Again
            </Button>
          </div>
        );

      default:
        return null;
    }
  };

  const questionStyle = {
    fontSize: 20,
    fontWeight: 600,
    marginBottom: 30,
    color: "#111827"
  };

  return (
    <div style={{
      minHeight: "100vh",
      backgroundColor: "#f3f4f6",
      paddingTop: 80,
      paddingBottom: 80
    }}>
      <div style={{
        maxWidth: 880,
        margin: "0 auto",
        padding: "0 20px"
      }}>
        <Card style={{
          backgroundColor: "#ffffff",
          borderRadius: 14,
          boxShadow: "0 10px 30px rgba(0,0,0,0.06)",
          padding: 60
        }}>
          <CardContent style={{ padding: 0 }}>

            <Typography style={{ fontSize: 28, fontWeight: 700, marginBottom: 8 }}>
              UlapPayroll Plan Finder
            </Typography>

            <div style={{
              height: 3,
              width: 60,
              backgroundColor: "#FF7504",
              borderRadius: 2,
              marginBottom: 20
            }} />

            <LinearProgress
              variant="determinate"
              value={progress}
              style={{ height: 6, borderRadius: 4, marginBottom: 10 }}
            />

            <Typography style={{ fontSize: 14, color: "#6b7280", marginBottom: 40 }}>
              Step {step} of {totalSteps}
            </Typography>

            {renderStep()}

            {step < 10 && (
              <div style={{ marginTop: 40, display: "flex", justifyContent: "space-between" }}>
                {step > 1 ? (
                  <Button onClick={prevStep} style={{ border: "1px solid #d1d5db" }}>
                    Back
                  </Button>
                ) : <div />}

                <Button
                  onClick={nextStep}
                  disabled={!isStepValid()}
                  style={{
                    backgroundColor: isStepValid() ? "#FF7504" : "#e5e7eb",
                    color: isStepValid() ? "#fff" : "#9ca3af"
                  }}
                >
                  Next
                </Button>
              </div>
            )}

          </CardContent>
        </Card>
      </div>
    </div>
  );
};

export default PlanFinder;
