import React, { useState } from "react";

const PlanFinder = () => {
  const [step, setStep] = useState(1);
  const [answers, setAnswers] = useState({});

  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  const handleAnswer = (key, value) => {
    setAnswers(prev => ({
      ...prev,
      [key]: value,
    }));
  };

  const handleMultiSelect = (key, value) => {
    setAnswers(prev => {
      const existing = prev[key] || [];

      if (existing.includes(value)) {
        return {
          ...prev,
          [key]: existing.filter(item => item !== value),
        };
      } else {
        return {
          ...prev,
          [key]: [...existing, value],
        };
      }
    });
  };

  const calculateScore = () => {
    let total = 0;

    Object.keys(answers).forEach(key => {
      if (Array.isArray(answers[key])) {
        total += answers[key].reduce((a, b) => a + b, 0);
      } else {
        total += answers[key];
      }
    });

    // Complexity bonus
    if (answers.workerTypes && answers.workerTypes.length >= 3) {
      total += 2;
    }

    return total;
  };

  const getRecommendation = (score) => {
    if (score <= 10) {
      return { name: "ðŸŸ¢ Starter", price: "2,000 PHP" };
    } else if (score <= 20) {
      return { name: "ðŸ”µ Professional", price: "6,000 PHP" };
    } else {
      return { name: "ðŸŸ£ Enterprise", price: "13,000 PHP" };
    }
  };

  const renderStep = () => {
    switch (step) {

      case 1:
        return (
          <>
            <h2>How many employees do you have?</h2>
            <button onClick={() => handleAnswer("employeeCount", 1)}>1-20</button>
            <button onClick={() => handleAnswer("employeeCount", 2)}>21-50</button>
            <button onClick={() => handleAnswer("employeeCount", 3)}>50-150</button>
            <button onClick={() => handleAnswer("employeeCount", 4)}>151+</button>
            <br /><br />
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 2:
        return (
          <>
            <h2>Do you operate multiple branches?</h2>
            <button onClick={() => handleAnswer("branches", 0)}>No</button>
            <button onClick={() => handleAnswer("branches", 2)}>2-5</button>
            <button onClick={() => handleAnswer("branches", 3)}>6+</button>
            <br /><br />
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 3:
        return (
          <>
            <h2>How do you currently process payroll?</h2>
            <button onClick={() => handleAnswer("payrollSystem", 1)}>Manual / Spreadsheets</button>
            <button onClick={() => handleAnswer("payrollSystem", 2)}>Payroll Software</button>
            <button onClick={() => handleAnswer("payrollSystem", 3)}>ERP with Payroll Software</button>
            <br /><br />
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 4:
        return (
          <>
            <h2>What types of workers do you pay?</h2>

            <button onClick={() => handleMultiSelect("workerTypes", 1)}>Fixed Monthly (1)</button>
            <button onClick={() => handleMultiSelect("workerTypes", 2)}>Daily Wage (2)</button>
            <button onClick={() => handleMultiSelect("workerTypes", 2)}>Project-Based (2)</button>
            <button onClick={() => handleMultiSelect("workerTypes", 3)}>Field / Remote (3)</button>
            <button onClick={() => handleMultiSelect("workerTypes", 3)}>Mixed Compensation (3)</button>

            <br /><br />
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 5:
        return (
          <>
            <h2>What do you want from your payroll solution?</h2>
            <button onClick={() => handleAnswer("featureLevel", 1)}>Standard features only</button>
            <button onClick={() => handleAnswer("featureLevel", 2)}>Standard + additional tools</button>
            <button onClick={() => handleAnswer("featureLevel", 3)}>Everything available</button>
            <br /><br />
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 6:
        return (
          <>
            <h2>What kind of payroll reports do you need?</h2>
            <button onClick={() => handleAnswer("reportType", 1)}>Standard reports only</button>
            <button onClick={() => handleAnswer("reportType", 2)}>Advanced compliance reports</button>
            <button onClick={() => handleAnswer("reportType", 3)}>Management & analytics reports</button>
            <br /><br />
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 7:
        return (
          <>
            <h2>Do you need approval workflows?</h2>
            <button onClick={() => handleAnswer("workflow", 2)}>Yes - simple approval</button>
            <button onClick={() => handleAnswer("workflow", 3)}>Yes - multi-level approval</button>
            <button onClick={() => handleAnswer("workflow", 0)}>No</button>
            <br /><br />
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );
      case 8:
  return (
    <>
      <h2>Do employees need a self-service portal?</h2>

      <button onClick={() => handleAnswer("selfService", 3)}>
        Yes - full self-service access
      </button>

      <button onClick={() => handleAnswer("selfService", 2)}>
        Yes - payslip access only
      </button>

      <button onClick={() => handleAnswer("selfService", 0)}>
        No
      </button>

      <br /><br />
      <button onClick={prevStep}>Back</button>
      <button onClick={nextStep}>Next</button>
    </>
  );  
      case 9:
  return (
    <>
      <h2>What level of payroll support would you like?</h2>

      <button onClick={() => handleAnswer("supportLevel", 2)}>
        Priority support
      </button>

      <button onClick={() => handleAnswer("supportLevel", 3)}>
        24/7 Product expert support
      </button>

      <br /><br />
      <button onClick={prevStep}>Back</button>
      <button onClick={nextStep}>See My Plan</button>
    </>
  );
      case 10:
        const score = calculateScore();
        const recommendation = getRecommendation(score);

        return (
          <>
            <h2>Your Recommended Plan</h2>
            <h3>{recommendation.name}</h3>
            <p>{recommendation.price} / month</p>
            <p>Total Score: {score}</p>

            <button onClick={prevStep}>Back</button>
          </>
        );

      default:
        return null;
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>UlapPayroll Plan Finder</h1>
      <p>Step {step}</p>
      {renderStep()}
    </div>
  );
};

export default PlanFinder;
