import React, { useState } from "react";
import {
  Typography,
  Box,
  Button
} from "@material-ui/core";

import "./ProductFinder.css";
import { calculatePlan } from "./scoring";

const TOTAL_STEPS = 12; // 0â€“11

const ProductFinder = () => {

  const [step, setStep] = useState(0);

  const [answers, setAnswers] = useState({
    employeeSize: 0,
    branches: 0,
    payrollSystem: 0,
    workerTypes: [],
    features: 0,
    reports: 0,
    approvals: 0,
    selfService: 0,
    support: 0,
  });

  const [result, setResult] = useState(null);

  // âœ… NEW CONTACT STATE
  const [contact, setContact] = useState({
    companyName: "",
    fullName: "",
    email: "",
    phone: "",
  });

  const nextStep = () => {
    if (step === 8) {
      const outcome = calculatePlan(answers);
      setResult(outcome);
      setStep(9);
    } else if (step < 9) {
      setStep(step + 1);
    }
  };

  const prevStep = () => {
    if (step > 0) {
      setStep(step - 1);
    }
  };

  const handleWorkerType = (label, score) => {
    const exists = answers.workerTypes.find(w => w.label === label);

    if (exists) {
      setAnswers({
        ...answers,
        workerTypes: answers.workerTypes.filter(w => w.label !== label)
      });
    } else {
      setAnswers({
        ...answers,
        workerTypes: [...answers.workerTypes, { label, score }]
      });
    }
  };

  const SurveyCard = ({ title, field, options }) => (
    <Box className="section">
      <Typography variant="h5">{title}</Typography>

      <div className="option-grid">
        {options.map(option => (
          <div
            key={option.label}
            className={`option-card ${
              answers[field] === option.value ? "selected" : ""
            }`}
            onClick={() =>
              setAnswers({ ...answers, [field]: option.value })
            }
          >
            {option.label}
          </div>
        ))}
      </div>
    </Box>
  );

  const MultiSurveyCard = ({ title, options }) => (
    <Box className="section">
      <Typography variant="h5">{title}</Typography>

      <div className="option-grid">
        {options.map(option => {
          const isSelected = answers.workerTypes.some(
            w => w.label === option.label
          );

          return (
            <div
              key={option.label}
              className={`option-card ${isSelected ? "selected" : ""}`}
              onClick={() => handleWorkerType(option.label, option.score)}
            >
              {option.label}
            </div>
          );
        })}
      </div>
    </Box>
  );

  const ResultCard = ({ result }) => (
    <Box className="result-card">
      <Typography variant="h4" gutterBottom>
        {result?.recommendation?.plan}
      </Typography>

      <Typography variant="h6">
        {result?.recommendation?.price}
      </Typography>

      <Typography style={{ marginTop: "1rem" }}>
        Based on your operational structure and payroll complexity,
        this plan best fits your business.
      </Typography>

      {/* âœ… PROCEED BUTTON NOW GOES TO STEP 10 */}
      <Button
        variant="contained"
        color="secondary"
        style={{ marginTop: "1.5rem" }}
        onClick={() => setStep(10)}
      >
        Proceed to Checkout
      </Button>
    </Box>
  );

  const renderStep = () => {
    switch (step) {

      case 0:
        return (
          <SurveyCard
            title="How many employees do you have?"
            field="employeeSize"
            options={[
              { label: "1â€“20 Employees", value: 1 },
              { label: "21â€“50 Employees", value: 2 },
              { label: "50â€“150 Employees", value: 3 },
              { label: "151+ Employees", value: 4 }
            ]}
          />
        );

      case 1:
        return (
          <SurveyCard
            title="Do you operate multiple branches?"
            field="branches"
            options={[
              { label: "No", value: 0 },
              { label: "2â€“5 Branches", value: 2 },
              { label: "6+ Branches", value: 3 }
            ]}
          />
        );

      case 2:
        return (
          <SurveyCard
            title="How do you currently process payroll?"
            field="payrollSystem"
            options={[
              { label: "Manually / Spreadsheets", value: 1 },
              { label: "Payroll Software", value: 2 },
              { label: "ERP with Payroll Module", value: 3 }
            ]}
          />
        );

      case 3:
        return (
          <MultiSurveyCard
            title="What types of workers do you pay?"
            options={[
              { label: "Fixed Monthly Employees", score: 1 },
              { label: "Daily Wage Employees", score: 2 },
              { label: "Project-Based Employees", score: 2 },
              { label: "Field / Remote Employees", score: 3 },
              { label: "Mixed Compensation Staff", score: 3 }
            ]}
          />
        );

      case 4:
        return (
          <SurveyCard
            title="What do you want from your payroll & HR solution?"
            field="features"
            options={[
              { label: "Standard features only", value: 1 },
              { label: "Standard + additional tools", value: 2 },
              { label: "Everything available", value: 3 }
            ]}
          />
        );

      case 5:
        return (
          <SurveyCard
            title="What kind of payroll reports do you need?"
            field="reports"
            options={[
              { label: "Standard reports", value: 1 },
              { label: "Advanced compliance reports", value: 2 },
              { label: "Management & analytics reports", value: 3 }
            ]}
          />
        );

      case 6:
        return (
          <SurveyCard
            title="Do you need approval workflows?"
            field="approvals"
            options={[
              { label: "No", value: 0 },
              { label: "Yes â€“ simple approval", value: 2 },
              { label: "Yes â€“ multi-level approval", value: 3 }
            ]}
          />
        );

      case 7:
        return (
          <SurveyCard
            title="Do employees need a self-service portal?"
            field="selfService"
            options={[
              { label: "No", value: 0 },
              { label: "Payslip access only", value: 2 },
              { label: "Full self-service access", value: 3 }
            ]}
          />
        );

      case 8:
        return (
          <SurveyCard
            title="What level of payroll support would you like?"
            field="support"
            options={[
              { label: "Priority support", value: 2 },
              { label: "24/7 Product expert support", value: 3 }
            ]}
          />
        );

      case 9:
        return <ResultCard result={result} />;

      // âœ… CONTACT FORM
      case 10:
        return (
          <Box className="section">
            <Typography variant="h5" gutterBottom>
              Provide Your Company Details
            </Typography>

            <Box mt={2}>
              <input
                type="text"
                placeholder="Company Name"
                value={contact.companyName}
                onChange={(e) =>
                  setContact({ ...contact, companyName: e.target.value })
                }
                className="input-field"
              />
            </Box>

            <Box mt={2}>
              <input
                type="text"
                placeholder="Full Name"
                value={contact.fullName}
                onChange={(e) =>
                  setContact({ ...contact, fullName: e.target.value })
                }
                className="input-field"
              />
            </Box>

            <Box mt={2}>
              <input
                type="email"
                placeholder="Email Address"
                value={contact.email}
                onChange={(e) =>
                  setContact({ ...contact, email: e.target.value })
                }
                className="input-field"
              />
            </Box>

            <Box mt={2}>
              <input
                type="text"
                placeholder="Phone Number"
                value={contact.phone}
                onChange={(e) =>
                  setContact({ ...contact, phone: e.target.value })
                }
                className="input-field"
              />
            </Box>

            <Box mt={3}>
              <Button
                variant="contained"
                color="primary"
                onClick={() => setStep(11)}
                disabled={
                  !contact.companyName ||
                  !contact.fullName ||
                  !contact.email ||
                  !contact.phone
                }
              >
                Confirm & Checkout
              </Button>
            </Box>
          </Box>
        );

      // âœ… THANK YOU SCREEN
      case 11:
        return (
          <Box className="result-card">
            <Typography variant="h4" gutterBottom>
              ðŸŽ‰ Thank You!
            </Typography>

            <Typography>
              Thank you for choosing UlapPayroll.
              Our team will contact you shortly to assist you further.
            </Typography>

            <Button
              variant="contained"
              color="primary"
              style={{ marginTop: "2rem" }}
              onClick={() => window.location.reload()}
            >
              Start Again
            </Button>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <div className="finder-wrapper">
      <div className="finder-card">

        <Typography variant="h4" gutterBottom>
          UlapPayroll Plan Recommendation Survey
        </Typography>

        {step <= 8 && (
          <Typography className="progress">
            Step {step + 1} of 9
          </Typography>
        )}

        {renderStep()}

        {step <= 8 && (
          <div className="navigation-buttons">
            <Button disabled={step === 0} onClick={prevStep}>
              Back
            </Button>

            <Button
              variant="contained"
              color="primary"
              onClick={nextStep}
            >
              Next
            </Button>
          </div>
        )}

      </div>
    </div>
  );
};

export default ProductFinder;
