import React, { useState, useCallback } from 'react';
import { computeTotalBasicEarned, compute13thMonthPay } from '../utils/thirteenthMonthCalc';
import './Calculator.css';

const getYearStart = () => {
  const d = new Date();
  return new Date(d.getFullYear(), 0, 1);
};
const getYearEnd = () => {
  const d = new Date();
  return new Date(d.getFullYear(), 11, 31);
};

function formatDate(d) {
  return d.toISOString().slice(0, 10);
}

function formatCurrency(n) {
  return new Intl.NumberFormat('en-PH', { style: 'currency', currency: 'PHP' }).format(n);
}

function Calculator() {
  const [monthlySalary, setMonthlySalary] = useState('');
  const [startDate, setStartDate] = useState(formatDate(getYearStart()));
  const [endDate, setEndDate] = useState(formatDate(getYearEnd()));
  const [unpaidDays, setUnpaidDays] = useState('0');
  const [allowances, setAllowances] = useState('0');
  const [copied, setCopied] = useState(false);

  const monthlyNum = parseFloat(monthlySalary) || 0;
  const start = new Date(startDate);
  const end = new Date(endDate);
  const unpaid = Math.max(0, parseInt(unpaidDays, 10) || 0);
  const allowancesNum = parseFloat(allowances) || 0;

  const { totalBasicEarned, dailyRate, unpaidDeduction } = computeTotalBasicEarned(
    monthlyNum,
    start,
    end,
    unpaid
  );
  const totalFor13th = totalBasicEarned + allowancesNum;
  const thirteenthMonth = compute13thMonthPay(totalFor13th);

  const handleCopy = useCallback(() => {
    const text = formatCurrency(thirteenthMonth);
    if (navigator.clipboard) {
      navigator.clipboard.writeText(text).then(() => {
        setCopied(true);
        setTimeout(() => setCopied(false), 1500);
      });
    }
  }, [thirteenthMonth]);

  const handleReset = useCallback(() => {
    setMonthlySalary('');
    setStartDate(formatDate(getYearStart()));
    setEndDate(formatDate(getYearEnd()));
    setUnpaidDays('0');
    setAllowances('0');
  }, []);

  return (
    <div className="calculator">
      <h1 className="calculator-title">13th Month Pay Calculator</h1>

      <section className="calculator-form">
        <div className="form-group">
          <label htmlFor="monthly-salary">Monthly Basic Salary (PHP)</label>
          <input
            id="monthly-salary"
            type="number"
            min="0"
            step="0.01"
            placeholder="e.g. 25000"
            value={monthlySalary}
            onChange={(e) => setMonthlySalary(e.target.value)}
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label htmlFor="start-date">Employment Start Date</label>
            <input
              id="start-date"
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label htmlFor="end-date">Employment End Date</label>
            <input
              id="end-date"
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
            />
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="allowances">Allowances (optional, PHP)</label>
          <input
            id="allowances"
            type="number"
            min="0"
            step="0.01"
            placeholder="0"
            value={allowances}
            onChange={(e) => setAllowances(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="unpaid-days">Unpaid Absence Days (optional)</label>
          <input
            id="unpaid-days"
            type="number"
            min="0"
            step="1"
            placeholder="0"
            value={unpaidDays}
            onChange={(e) => setUnpaidDays(e.target.value)}
          />
        </div>

        <button type="button" className="btn-reset" onClick={handleReset}>
          Reset
        </button>
      </section>

      <section className="calculator-results">
        <h2>Results</h2>
        <div className="results-grid">
          <div className="result-item">
            <span className="result-label">Total Basic Salary Earned</span>
            <span className="result-value">{formatCurrency(totalBasicEarned)}</span>
          </div>
          {allowancesNum > 0 && (
            <div className="result-item">
              <span className="result-label">Allowances</span>
              <span className="result-value">+ {formatCurrency(allowancesNum)}</span>
            </div>
          )}
          {unpaid > 0 && (
            <div className="result-item highlight-warning">
              <span className="result-label">Unpaid Absence Deduction</span>
              <span className="result-value">- {formatCurrency(unpaidDeduction)}</span>
            </div>
          )}
          <div className="result-item primary">
            <div className="result-13th-row">
              <span className="result-label">13th Month Pay</span>
              <span className="result-value result-13th">{formatCurrency(thirteenthMonth)}</span>
            </div>
            {monthlyNum > 0 && (
              <button
                type="button"
                className={`btn-copy ${copied ? 'copied' : ''}`}
                onClick={handleCopy}
                title="Copy amount"
              >
                {copied ? 'Copied!' : 'Copy'}
              </button>
            )}
          </div>
        </div>
        {monthlyNum > 0 && (
          <p className="helper-text">
            Daily rate (รท22): {formatCurrency(dailyRate)}/day
          </p>
        )}
      </section>
    </div>
  );
}

export default Calculator;
