import React, { useState } from "react";
import {
  Typography,
  Box,
  Button,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Checkbox
} from "@material-ui/core";

import "./ProductFinder.css";
import { calculatePlan } from "./scoring";

const ProductFinder = () => {

  const [answers, setAnswers] = useState({
    employeeSize: 0,
    workerTypes: [],
  });

  const [result, setResult] = useState(null);

  const handleWorkerType = (label, score) => {
    const exists = answers.workerTypes.find(w => w.label === label);

    if (exists) {
      setAnswers({
        ...answers,
        workerTypes: answers.workerTypes.filter(w => w.label !== label)
      });
    } else {
      setAnswers({
        ...answers,
        workerTypes: [...answers.workerTypes, { label, score }]
      });
    }
  };

  const handleSubmit = () => {
    const outcome = calculatePlan({
      ...answers,
      branches: 0,
      payrollSystem: 0,
      features: 0,
      reports: 0,
      approvals: 0,
      selfService: 0,
      support: 0,
    });

    setResult(outcome);
  };

  return (
    <div className="finder-wrapper">
      <div className="finder-card">

        <Typography variant="h3" gutterBottom>
          UlapPayroll Plan Recommendation Survey
        </Typography>

        <Typography variant="subtitle1" style={{ marginBottom: "2rem" }}>
          Help us understand your business so we can recommend the best UlapPayroll plan for you.
        </Typography>

        {/* Company Size */}
        <Box className="section">
          <Typography variant="h5" className="section-title">
            Company Size
          </Typography>

          <FormControl>
            <FormLabel>How many employees do you have?</FormLabel>
            <RadioGroup
              onChange={(e) =>
                setAnswers({ ...answers, employeeSize: Number(e.target.value) })
              }
            >
              <FormControlLabel value={1} control={<Radio />} label="1-20" />
              <FormControlLabel value={2} control={<Radio />} label="21-50" />
              <FormControlLabel value={3} control={<Radio />} label="50-150" />
              <FormControlLabel value={4} control={<Radio />} label="151+" />
            </RadioGroup>
          </FormControl>
        </Box>

        {/* Worker Types */}
        <Box className="section">
          <Typography variant="h5" className="section-title">
            Workforce Structure
          </Typography>

          <FormLabel>What types of workers do you pay?</FormLabel>

          <FormControlLabel
            control={<Checkbox onChange={() => handleWorkerType("Fixed Monthly Employees", 1)} />}
            label="Fixed Monthly Employees"
          />
          <FormControlLabel
            control={<Checkbox onChange={() => handleWorkerType("Daily Wage Employees", 2)} />}
            label="Daily Wage Employees"
          />
          <FormControlLabel
            control={<Checkbox onChange={() => handleWorkerType("Project-Based Employees", 2)} />}
            label="Project-Based Employees"
          />
          <FormControlLabel
            control={<Checkbox onChange={() => handleWorkerType("Field / Remote Employees", 3)} />}
            label="Field / Remote Employees"
          />
          <FormControlLabel
            control={<Checkbox onChange={() => handleWorkerType("Mixed Compensation Staff", 3)} />}
            label="Mixed Compensation Staff"
          />
        </Box>

        <Box textAlign="center">
          <Button
            variant="contained"
            color="primary"
            size="large"
            onClick={handleSubmit}
          >
            See My Recommended Plan
          </Button>
        </Box>

        {result && (
          <Box className="result-card">
            <Typography variant="h4" gutterBottom>
              {result.recommendation.plan}
            </Typography>

            <Typography variant="h6">
              {result.recommendation.price}
            </Typography>

            <Typography style={{ marginTop: "1rem" }}>
              Based on your answers, this plan best fits your companyâ€™s payroll structure and operational complexity.
            </Typography>

            <Button
              variant="contained"
              color="secondary"
              className="checkout-btn"
            >
              Proceed to Checkout
            </Button>
          </Box>
        )}

      </div>
    </div>
  );
};

export default ProductFinder;
