import React, { useState } from "react";



const PlanFinder = () => {
  const [step, setStep] = useState(1);
  const [answers, setAnswers] = useState({});

  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  const handleAnswer = (key, value) => {
    setAnswers(prev => ({
      ...prev,
      [key]: value,
    }));
  };

const calculateScore = () => {
  let total = 0;

  Object.keys(answers).forEach(key => {
    total += answers[key];
  });

  return total;
};

const getRecommendation = (score) => {
  if (score <= 10) {
    return {
      name: "ðŸŸ¢ Starter",
      price: "2,000 PHP",
    };
  } else if (score <= 20) {
    return {
      name: "ðŸ”µ Professional",
      price: "6,000 PHP",
    };
  } else {
    return {
      name: "ðŸŸ£ Enterprise",
      price: "13,000 PHP",
    };
  }
};


  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <>
            <h2>How many employees do you have?</h2>
            <button onClick={() => handleAnswer("employeeCount", 1)}>1-20</button>
            <button onClick={() => handleAnswer("employeeCount", 2)}>21-50</button>
            <button onClick={() => handleAnswer("employeeCount", 3)}>50-150</button>
            <button onClick={() => handleAnswer("employeeCount", 4)}>151+</button>
            <br /><br />
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 2:
        return (
          <>
            <h2>Do you operate multiple branches?</h2>
            <button onClick={() => handleAnswer("branches", 0)}>No</button>
            <button onClick={() => handleAnswer("branches", 2)}>2-5</button>
            <button onClick={() => handleAnswer("branches", 3)}>6+</button>
            <br /><br />
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 3:
        return (
          <>
            <h2>Review</h2>
            <pre>{JSON.stringify(answers, null, 2)}</pre>
            <button onClick={prevStep}>Back</button>
          </>
        );

      default:
        return null;
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>UlapPayroll Plan Finder</h1>
      <p>Step {step}</p>
      {renderStep()}
    </div>
  );
};

export default PlanFinder;
