import React, { useState } from "react";
import {
  Button,
  LinearProgress,
  Card,
  CardContent,
  Typography,
  TextField,
  Grid
} from "@material-ui/core";

const totalSteps = 11;

const PlanFinder = () => {
  const [step, setStep] = useState(1);
  const [answers, setAnswers] = useState({});
  const [leadInfo, setLeadInfo] = useState({
    name: "",
    email: "",
    company: "",
  });

  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  const handleAnswer = (key, value) => {
    setAnswers(prev => ({ ...prev, [key]: value }));
  };

  const handleMultiSelect = (key, value) => {
    setAnswers(prev => {
      const existing = prev[key] || [];
      if (existing.includes(value)) {
        return { ...prev, [key]: existing.filter(v => v !== value) };
      }
      return { ...prev, [key]: [...existing, value] };
    });
  };

  const handleLeadChange = (e) => {
    setLeadInfo({ ...leadInfo, [e.target.name]: e.target.value });
  };

  const isStepValid = () => {
    switch (step) {
      case 1: return answers.employeeCount !== undefined;
      case 2: return answers.branches !== undefined;
      case 3: return answers.payrollSystem !== undefined;
      case 4: return answers.workerTypes && answers.workerTypes.length > 0;
      case 5: return answers.featureLevel !== undefined;
      case 6: return answers.reportType !== undefined;
      case 7: return answers.workflow !== undefined;
      case 8: return answers.selfService !== undefined;
      case 9: return answers.supportLevel !== undefined;
      case 11: return leadInfo.name && leadInfo.email && leadInfo.company;
      default: return true;
    }
  };

  const calculateScore = () => {
    let total = 0;

    Object.keys(answers).forEach(key => {
      if (Array.isArray(answers[key])) {
        total += answers[key].reduce((a, b) => a + b, 0);
      } else {
        total += answers[key];
      }
    });

    if (answers.workerTypes && answers.workerTypes.length >= 3) {
      total += 2;
    }

    return total;
  };

  const getRecommendation = (score) => {
    if (score <= 10) return { name: "ðŸŸ¢ Starter", price: "2,000 PHP" };
    if (score <= 20) return { name: "ðŸ”µ Professional", price: "6,000 PHP" };
    return { name: "ðŸŸ£ Enterprise", price: "13,000 PHP" };
  };

  const progress = (step / totalSteps) * 100;

  const OptionButton = ({ selected, onClick, children }) => (
    <Button
      variant={selected ? "contained" : "outlined"}
      color="primary"
      onClick={onClick}
      fullWidth
      style={{ marginBottom: 10 }}
    >
      {children}
    </Button>
  );

  const renderStep = () => {
    switch (step) {

      case 1:
        return (
          <>
            <Typography variant="h6">How many employees do you have?</Typography>
            <OptionButton selected={answers.employeeCount === 1} onClick={() => handleAnswer("employeeCount", 1)}>1-20</OptionButton>
            <OptionButton selected={answers.employeeCount === 2} onClick={() => handleAnswer("employeeCount", 2)}>21-50</OptionButton>
            <OptionButton selected={answers.employeeCount === 3} onClick={() => handleAnswer("employeeCount", 3)}>50-150</OptionButton>
            <OptionButton selected={answers.employeeCount === 4} onClick={() => handleAnswer("employeeCount", 4)}>151+</OptionButton>
          </>
        );

      case 2:
        return (
          <>
            <Typography variant="h6">Do you operate multiple branches?</Typography>
            <OptionButton selected={answers.branches === 0} onClick={() => handleAnswer("branches", 0)}>No</OptionButton>
            <OptionButton selected={answers.branches === 2} onClick={() => handleAnswer("branches", 2)}>2-5 Branches</OptionButton>
            <OptionButton selected={answers.branches === 3} onClick={() => handleAnswer("branches", 3)}>6+ Branches</OptionButton>
          </>
        );

      case 3:
        return (
          <>
            <Typography variant="h6">How do you currently process payroll?</Typography>
            <OptionButton selected={answers.payrollSystem === 1} onClick={() => handleAnswer("payrollSystem", 1)}>Manual / Spreadsheets</OptionButton>
            <OptionButton selected={answers.payrollSystem === 2} onClick={() => handleAnswer("payrollSystem", 2)}>Payroll Software</OptionButton>
            <OptionButton selected={answers.payrollSystem === 3} onClick={() => handleAnswer("payrollSystem", 3)}>ERP with Payroll</OptionButton>
          </>
        );

      case 4:
  return (
    <>
      <Typography variant="h6">What types of workers do you pay?</Typography>

      <OptionButton
        selected={answers.workerTypes?.includes("fixed")}
        onClick={() => handleMultiSelect("workerTypes", "fixed")}
      >
        Fixed Monthly
      </OptionButton>

      <OptionButton
        selected={answers.workerTypes?.includes("daily")}
        onClick={() => handleMultiSelect("workerTypes", "daily")}
      >
        Daily Wage
      </OptionButton>

      <OptionButton
        selected={answers.workerTypes?.includes("project")}
        onClick={() => handleMultiSelect("workerTypes", "project")}
      >
        Project-Based
      </OptionButton>

      <OptionButton
        selected={answers.workerTypes?.includes("field")}
        onClick={() => handleMultiSelect("workerTypes", "field")}
      >
        Field / Remote
      </OptionButton>

      <OptionButton
        selected={answers.workerTypes?.includes("mixed")}
        onClick={() => handleMultiSelect("workerTypes", "mixed")}
      >
        Mixed Compensation
      </OptionButton>
    </>
  );

      case 5:
        return (
          <>
            <Typography variant="h6">What do you want from your payroll solution?</Typography>
            <OptionButton selected={answers.featureLevel === 1} onClick={() => handleAnswer("featureLevel", 1)}>Standard Features</OptionButton>
            <OptionButton selected={answers.featureLevel === 2} onClick={() => handleAnswer("featureLevel", 2)}>Standard + Tools</OptionButton>
            <OptionButton selected={answers.featureLevel === 3} onClick={() => handleAnswer("featureLevel", 3)}>Everything Available</OptionButton>
          </>
        );

      case 6:
        return (
          <>
            <Typography variant="h6">What kind of payroll reports do you need?</Typography>
            <OptionButton selected={answers.reportType === 1} onClick={() => handleAnswer("reportType", 1)}>Standard Reports</OptionButton>
            <OptionButton selected={answers.reportType === 2} onClick={() => handleAnswer("reportType", 2)}>Compliance Reports</OptionButton>
            <OptionButton selected={answers.reportType === 3} onClick={() => handleAnswer("reportType", 3)}>Management & Analytics</OptionButton>
          </>
        );

      case 7:
        return (
          <>
            <Typography variant="h6">Do you need approval workflows?</Typography>
            <OptionButton selected={answers.workflow === 2} onClick={() => handleAnswer("workflow", 2)}>Simple Approval</OptionButton>
            <OptionButton selected={answers.workflow === 3} onClick={() => handleAnswer("workflow", 3)}>Multi-Level Approval</OptionButton>
            <OptionButton selected={answers.workflow === 0} onClick={() => handleAnswer("workflow", 0)}>No</OptionButton>
          </>
        );

      case 8:
        return (
          <>
            <Typography variant="h6">Do employees need self-service access?</Typography>
            <OptionButton selected={answers.selfService === 3} onClick={() => handleAnswer("selfService", 3)}>Full Access</OptionButton>
            <OptionButton selected={answers.selfService === 2} onClick={() => handleAnswer("selfService", 2)}>Payslip Only</OptionButton>
            <OptionButton selected={answers.selfService === 0} onClick={() => handleAnswer("selfService", 0)}>No</OptionButton>
          </>
        );

      case 9:
        return (
          <>
            <Typography variant="h6">What level of payroll support?</Typography>
            <OptionButton selected={answers.supportLevel === 2} onClick={() => handleAnswer("supportLevel", 2)}>Priority Support</OptionButton>
            <OptionButton selected={answers.supportLevel === 3} onClick={() => handleAnswer("supportLevel", 3)}>24/7 Expert Support</OptionButton>
          </>
        );

      case 10:
        const score = calculateScore();
        const recommendation = getRecommendation(score);
        return (
          <div style={{ textAlign: "center" }}>
            <Typography variant="h5">ðŸŽ‰ Your Recommended Plan</Typography>
            <Typography variant="h4">{recommendation.name}</Typography>
            <Typography variant="h6">{recommendation.price} / month</Typography>
            <Typography>Total Score: {score}</Typography>
            <Button variant="contained" color="primary" style={{ marginTop: 20 }} onClick={() => setStep(11)}>
              Avail This Plan
            </Button>
          </div>
        );

      case 11:
        return (
          <>
            <Typography variant="h6">Enter Your Details</Typography>
            <TextField label="Full Name" name="name" fullWidth margin="normal" onChange={handleLeadChange} />
            <TextField label="Email" name="email" fullWidth margin="normal" onChange={handleLeadChange} />
            <TextField label="Company" name="company" fullWidth margin="normal" onChange={handleLeadChange} />
            <Button variant="contained" color="primary" disabled={!isStepValid()} style={{ marginTop: 15 }}>
              Submit Inquiry
            </Button>
          </>
        );

      default:
        return null;
    }
  };

  return (
<div
  style={{
    minHeight: "100vh",
    background: "linear-gradient(135deg, #f5f7fa 0%, #e4ecf7 100%)",
    paddingTop: "80px",
    paddingBottom: "80px",
  }}
>
      <Card
    style={{
      width: "900px",
      padding: "3rem",
      borderRadius: "16px",
      boxShadow: "0 20px 40px rgba(0,0,0,0.08)"
    }}
  >
        <CardContent>
          <Typography variant="h4" gutterBottom>
            UlapPayroll Plan Finder
          </Typography>

          <LinearProgress variant="determinate" value={progress} />
          <Typography variant="body2" style={{ marginTop: 8 }}>
            Step {step} of {totalSteps}
          </Typography>

          <div style={{ marginTop: 20 }}>
            {renderStep()}
          </div>

          {step < 10 && (
            <Grid container spacing={2} style={{ marginTop: 20 }}>
              {step > 1 && (
                <Grid item>
                  <Button variant="outlined" onClick={prevStep}>
                    Back
                  </Button>
                </Grid>
              )}
              <Grid item>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={nextStep}
                  disabled={!isStepValid()}
                >
                  Next
                </Button>
              </Grid>
            </Grid>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default PlanFinder;
