import React, { useState } from "react";
import { Typography, Box, Button } from "@material-ui/core";
import "./ProductFinder.css";
import { calculatePlan } from "./scoring";

/* =========================
   STEP CONSTANTS
========================= */
const TOTAL_QUESTION_STEPS = 9;
const RESULT_STEP = 9;
const CONTACT_STEP = 10;
const THANKYOU_STEP = 11;
const PLAN_COMPARE_STEP = 12;

const ProductFinder = () => {
  const [step, setStep] = useState(0);

  const [answers, setAnswers] = useState({
    employeeSize: 0,
    branches: 0,
    payrollSystem: 0,
    workerTypes: [],
    features: 0,
    reports: 0,
    approvals: 0,
    selfService: 0,
    support: 0,
  });

  const [result, setResult] = useState(null);

  const [contact, setContact] = useState({
    companyName: "",
    fullName: "",
    email: "",
    phone: "",
  });

  /* =========================
     NAVIGATION
  ========================= */
  const nextStep = () => {
    if (step === TOTAL_QUESTION_STEPS - 1) {
      const outcome = calculatePlan(answers);
      setResult(outcome);
      setStep(RESULT_STEP);
    } else {
      setStep(step + 1);
    }
  };

  const prevStep = () => {
    if (step > 0) setStep(step - 1);
  };

  const resetAll = () => {
    setStep(0);
    setAnswers({
      employeeSize: 0,
      branches: 0,
      payrollSystem: 0,
      workerTypes: [],
      features: 0,
      reports: 0,
      approvals: 0,
      selfService: 0,
      support: 0,
    });
    setResult(null);
    setContact({
      companyName: "",
      fullName: "",
      email: "",
      phone: "",
    });
  };

  const isStepValid = () => {
    switch (step) {
      case 0:
        return answers.employeeSize !== 0;
      case 2:
        return answers.payrollSystem !== 0;
      case 3:
        return answers.workerTypes.length > 0;
      case 4:
        return answers.features !== 0;
      case 5:
        return answers.reports !== 0;
      case 8:
        return answers.support !== 0;
      default:
        return true;
    }
  };

  const handleWorkerType = (label, score) => {
    const exists = answers.workerTypes.find((w) => w.label === label);
    if (exists) {
      setAnswers({
        ...answers,
        workerTypes: answers.workerTypes.filter(
          (w) => w.label !== label
        ),
      });
    } else {
      setAnswers({
        ...answers,
        workerTypes: [...answers.workerTypes, { label, score }],
      });
    }
  };

  /* =========================
     COMPONENTS
  ========================= */

  const SurveyCard = ({ title, field, options }) => (
    <Box className="section">
      <Typography variant="h5">{title}</Typography>
      <div className="option-grid">
        {options.map((option) => (
          <div
            key={option.label}
            className={`option-card ${
              answers[field] === option.value ? "selected" : ""
            }`}
            onClick={() =>
              setAnswers({ ...answers, [field]: option.value })
            }
          >
            {option.label}
          </div>
        ))}
      </div>
    </Box>
  );

  const MultiSurveyCard = ({ title, options }) => (
    <Box className="section">
      <Typography variant="h5">{title}</Typography>
      <div className="option-grid">
        {options.map((option) => {
          const isSelected = answers.workerTypes.some(
            (w) => w.label === option.label
          );
          return (
            <div
              key={option.label}
              className={`option-card ${
                isSelected ? "selected" : ""
              }`}
              onClick={() =>
                handleWorkerType(option.label, option.score)
              }
            >
              {option.label}
            </div>
          );
        })}
      </div>
    </Box>
  );

  const ResultCard = ({ result }) => (
    <Box className="result-card">
      <Typography variant="h4">
        {result?.recommendation?.plan}
      </Typography>
      <Typography variant="h6">
        {result?.recommendation?.price}
      </Typography>

      <div className="result-actions">
        <Button
          variant="contained"
          className="primary-cta"
          onClick={() => setStep(CONTACT_STEP)}
        >
          Proceed to Checkout
        </Button>

        <Button
          variant="outlined"
          className="secondary-cta"
          onClick={() => setStep(PLAN_COMPARE_STEP)}
        >
          Seek Another Option
        </Button>
      </div>
    </Box>
  );

  /* =========================
     STEP RENDERER
  ========================= */

  const renderStep = () => {
    switch (step) {

      case 0:
        return (
          <SurveyCard
            title="How many employees do you have?"
            field="employeeSize"
            options={[
              { label: "1‚Äì20 Employees", value: 1 },
              { label: "21‚Äì50 Employees", value: 2 },
              { label: "50‚Äì150 Employees", value: 3 },
              { label: "151+ Employees", value: 4 },
            ]}
          />
        );

      case 1:
        return (
          <SurveyCard
            title="Do you operate multiple branches?"
            field="branches"
            options={[
              { label: "No", value: 0 },
              { label: "2‚Äì5 Branches", value: 2 },
              { label: "6+ Branches", value: 3 },
            ]}
          />
        );

      case 2:
        return (
          <SurveyCard
            title="How do you currently process payroll?"
            field="payrollSystem"
            options={[
              { label: "Manually / Spreadsheets", value: 1 },
              { label: "Payroll Software", value: 2 },
              { label: "ERP with Payroll Module", value: 3 },
            ]}
          />
        );

      case RESULT_STEP:
        return <ResultCard result={result} />;

      case CONTACT_STEP:
        return (
          <div className="contact-wrapper">
            <button
              className="contact-back"
              onClick={() => setStep(RESULT_STEP)}
            >
              ‚Üê Back
            </button>

            <div className="contact-card">
              <h2>Company Details</h2>

              <div className="contact-grid">
                {[
                  { key: "companyName", label: "Company Name" },
                  { key: "fullName", label: "Full Name" },
                  { key: "email", label: "Email Address" },
                  { key: "phone", label: "Phone Number" },
                ].map((field) => (
                  <div className="input-group" key={field.key}>
                    <input
                      type="text"
                      placeholder=" "
                      value={contact[field.key]}
                      onChange={(e) =>
                        setContact({
                          ...contact,
                          [field.key]: e.target.value,
                        })
                      }
                    />
                    <label>{field.label}</label>
                  </div>
                ))}
              </div>

              <button
                className="complete-btn"
                disabled={
                  !contact.companyName ||
                  !contact.fullName ||
                  !contact.email ||
                  !contact.phone
                }
                onClick={() => setStep(THANKYOU_STEP)}
              >
                Complete Setup ‚Üí
              </button>
            </div>
          </div>
        );

      case THANKYOU_STEP:
        return (
          <Box className="result-card">
            <Typography variant="h4">
              üéâ Thank you for availing!
            </Typography>
            <Button
              variant="contained"
              className="primary-cta"
              onClick={resetAll}
              style={{ marginTop: "2rem" }}
            >
              Start Again
            </Button>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <div className="finder-wrapper">
      <div className="finder-card">

        {step < TOTAL_QUESTION_STEPS && (
          <div className="progress-container">
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{
                  width: `${
                    ((step + 1) / TOTAL_QUESTION_STEPS) * 100
                  }%`,
                }}
              />
            </div>
            <Typography className="progress-text">
              Step {step + 1} of {TOTAL_QUESTION_STEPS}
            </Typography>
          </div>
        )}

        {renderStep()}

        {step < TOTAL_QUESTION_STEPS && (
          <div className="navigation-buttons">
            <Button disabled={step === 0} onClick={prevStep}>
              Back
            </Button>

            <Button
              variant="contained"
              className="primary-cta"
              onClick={nextStep}
              disabled={!isStepValid()}
            >
              Next
            </Button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductFinder;
