import React, { useState } from "react";
import { Button, LinearProgress } from "@material-ui/core";

const totalSteps = 10;

const PlanFinder = () => {
  const [step, setStep] = useState(1);
  const [answers, setAnswers] = useState({});

  const nextStep = () => setStep(prev => prev + 1);
  const prevStep = () => setStep(prev => prev - 1);

  const handleAnswer = (key, value) => {
    setAnswers(prev => ({
      ...prev,
      [key]: value,
    }));
  };

  const handleMultiSelect = (key, value) => {
    setAnswers(prev => {
      const existing = prev[key] || [];
      if (existing.includes(value)) {
        return {
          ...prev,
          [key]: existing.filter(item => item !== value),
        };
      } else {
        return {
          ...prev,
          [key]: [...existing, value],
        };
      }
    });
  };

  const isStepValid = () => {
    switch (step) {
      case 1: return answers.employeeCount !== undefined;
      case 2: return answers.branches !== undefined;
      case 3: return answers.payrollSystem !== undefined;
      case 4: return answers.workerTypes && answers.workerTypes.length > 0;
      case 5: return answers.featureLevel !== undefined;
      case 6: return answers.reportType !== undefined;
      case 7: return answers.workflow !== undefined;
      case 8: return answers.selfService !== undefined;
      case 9: return answers.supportLevel !== undefined;
      default: return true;
    }
  };

  const calculateScore = () => {
    let total = 0;

    Object.keys(answers).forEach(key => {
      if (Array.isArray(answers[key])) {
        total += answers[key].reduce((a, b) => a + b, 0);
      } else {
        total += answers[key];
      }
    });

    if (answers.workerTypes && answers.workerTypes.length >= 3) {
      total += 2;
    }

    return total;
  };

  const getRecommendation = (score) => {
    if (score <= 10) {
      return { name: "ðŸŸ¢ Starter", price: "2,000 PHP" };
    } else if (score <= 20) {
      return { name: "ðŸ”µ Professional", price: "6,000 PHP" };
    } else {
      return { name: "ðŸŸ£ Enterprise", price: "13,000 PHP" };
    }
  };

  const progress = (step / totalSteps) * 100;

  const renderStep = () => {
    switch (step) {

      case 1:
        return (
          <>
            <h2>How many employees do you have?</h2>
            <Button variant="contained" onClick={() => handleAnswer("employeeCount", 1)}>1-20</Button>
            <Button variant="contained" onClick={() => handleAnswer("employeeCount", 2)}>21-50</Button>
            <Button variant="contained" onClick={() => handleAnswer("employeeCount", 3)}>50-150</Button>
            <Button variant="contained" onClick={() => handleAnswer("employeeCount", 4)}>151+</Button>
          </>
        );

      case 2:
        return (
          <>
            <h2>Do you operate multiple branches?</h2>
            <Button variant="contained" onClick={() => handleAnswer("branches", 0)}>No</Button>
            <Button variant="contained" onClick={() => handleAnswer("branches", 2)}>2-5</Button>
            <Button variant="contained" onClick={() => handleAnswer("branches", 3)}>6+</Button>
          </>
        );

      case 3:
        return (
          <>
            <h2>How do you currently process payroll?</h2>
            <Button variant="contained" onClick={() => handleAnswer("payrollSystem", 1)}>Manual</Button>
            <Button variant="contained" onClick={() => handleAnswer("payrollSystem", 2)}>Payroll Software</Button>
            <Button variant="contained" onClick={() => handleAnswer("payrollSystem", 3)}>ERP</Button>
          </>
        );

      case 4:
        return (
          <>
            <h2>What types of workers do you pay?</h2>
            <Button variant="outlined" onClick={() => handleMultiSelect("workerTypes", 1)}>Fixed</Button>
            <Button variant="outlined" onClick={() => handleMultiSelect("workerTypes", 2)}>Daily</Button>
            <Button variant="outlined" onClick={() => handleMultiSelect("workerTypes", 2)}>Project</Button>
            <Button variant="outlined" onClick={() => handleMultiSelect("workerTypes", 3)}>Field</Button>
            <Button variant="outlined" onClick={() => handleMultiSelect("workerTypes", 3)}>Mixed</Button>
          </>
        );

      case 5:
        return (
          <>
            <h2>Feature Level Needed</h2>
            <Button variant="contained" onClick={() => handleAnswer("featureLevel", 1)}>Standard</Button>
            <Button variant="contained" onClick={() => handleAnswer("featureLevel", 2)}>Standard + Tools</Button>
            <Button variant="contained" onClick={() => handleAnswer("featureLevel", 3)}>Everything</Button>
          </>
        );

      case 6:
        return (
          <>
            <h2>Report Type Needed</h2>
            <Button variant="contained" onClick={() => handleAnswer("reportType", 1)}>Standard</Button>
            <Button variant="contained" onClick={() => handleAnswer("reportType", 2)}>Compliance</Button>
            <Button variant="contained" onClick={() => handleAnswer("reportType", 3)}>Analytics</Button>
          </>
        );

      case 7:
        return (
          <>
            <h2>Workflow Approval Needed?</h2>
            <Button variant="contained" onClick={() => handleAnswer("workflow", 2)}>Simple</Button>
            <Button variant="contained" onClick={() => handleAnswer("workflow", 3)}>Multi-Level</Button>
            <Button variant="contained" onClick={() => handleAnswer("workflow", 0)}>No</Button>
          </>
        );

      case 8:
        return (
          <>
            <h2>Self-Service Portal Needed?</h2>
            <Button variant="contained" onClick={() => handleAnswer("selfService", 3)}>Full Access</Button>
            <Button variant="contained" onClick={() => handleAnswer("selfService", 2)}>Payslip Only</Button>
            <Button variant="contained" onClick={() => handleAnswer("selfService", 0)}>No</Button>
          </>
        );

      case 9:
        return (
          <>
            <h2>Support Level</h2>
            <Button variant="contained" onClick={() => handleAnswer("supportLevel", 2)}>Priority</Button>
            <Button variant="contained" onClick={() => handleAnswer("supportLevel", 3)}>24/7 Expert</Button>
          </>
        );

      case 10:
        const score = calculateScore();
        const recommendation = getRecommendation(score);

        return (
          <div style={{ textAlign: "center" }}>
            <h2>ðŸŽ‰ Your Recommended Plan</h2>
            <h1>{recommendation.name}</h1>
            <h3>{recommendation.price} / month</h3>
            <p>Total Score: {score}</p>
            <Button variant="contained" color="primary">
              Proceed to Checkout
            </Button>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div style={{ padding: "2rem", maxWidth: "600px", margin: "auto" }}>
      <h1>UlapPayroll Plan Finder</h1>
      <LinearProgress variant="determinate" value={progress} />
      <p>Step {step} of {totalSteps}</p>

      {renderStep()}

      {step < totalSteps && (
        <div style={{ marginTop: "20px" }}>
          {step > 1 && (
            <Button variant="outlined" onClick={prevStep}>
              Back
            </Button>
          )}
          <Button
            variant="contained"
            color="primary"
            onClick={nextStep}
            disabled={!isStepValid()}
            style={{ marginLeft: "10px" }}
          >
            {step === totalSteps - 1 ? "See My Plan" : "Next"}
          </Button>
        </div>
      )}
    </div>
  );
};

export default PlanFinder;
