import React from 'react';
import './ToolTemplate.css';

/**
 * Reusable template for payroll/tool pages.
 * Use for calculators, estimators, and lead-generation tools.
 *
 * @param {string} title - Tool title
 * @param {string} [subtitle] - Optional subtitle/description
 * @param {React.ReactNode} children - Main tool content
 * @param {React.ReactNode} [cta] - Optional CTA section (e.g., lead capture)
 */
function ToolTemplate({ title, subtitle, children, cta }) {
  return (
    <article className="tool-template">
      <header className="tool-template-hero">
        <h1 className="tool-template-title">{title}</h1>
        {subtitle && <p className="tool-template-subtitle">{subtitle}</p>}
      </header>
      <section className="tool-template-content">
        {children}
      </section>
      {cta && (
        <aside className="tool-template-cta">
          {cta}
        </aside>
      )}
    </article>
  );
}

export default ToolTemplate;
