import React, { useState } from "react";
import {
  Typography,
  Box,
  Button,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Checkbox
} from "@material-ui/core";

import "./ProductFinder.css";
import { calculatePlan } from "./scoring";

const TOTAL_STEPS = 3; // we’ll expand later

const ProductFinder = () => {

  const [step, setStep] = useState(0);

  const [answers, setAnswers] = useState({
    employeeSize: 0,
    workerTypes: [],
  });

  const [result, setResult] = useState(null);

  const nextStep = () => {
    if (step < TOTAL_STEPS) {
      setStep(step + 1);
    }
  };

  const prevStep = () => {
    if (step > 0) {
      setStep(step - 1);
    }
  };

  const handleWorkerType = (label, score) => {
    const exists = answers.workerTypes.find(w => w.label === label);

    if (exists) {
      setAnswers({
        ...answers,
        workerTypes: answers.workerTypes.filter(w => w.label !== label)
      });
    } else {
      setAnswers({
        ...answers,
        workerTypes: [...answers.workerTypes, { label, score }]
      });
    }
  };

  const handleSubmit = () => {
    const outcome = calculatePlan({
      ...answers,
      branches: 0,
      payrollSystem: 0,
      features: 0,
      reports: 0,
      approvals: 0,
      selfService: 0,
      support: 0,
    });

    setResult(outcome);
    setStep(TOTAL_STEPS);
  };

  const renderStep = () => {

    switch (step) {

      case 0:
  return (
    <Box className="section">
      <Typography variant="h5" gutterBottom>
        Company Size
      </Typography>

      <Typography>
        How many employees do you have?
      </Typography>

      <div className="option-grid">
        {[
          { label: "1-20", value: 1 },
          { label: "21-50", value: 2 },
          { label: "50-150", value: 3 },
          { label: "151+", value: 4 }
        ].map((option) => (
          <div
            key={option.label}
            className={`option-card ${
              answers.employeeSize === option.value ? "selected" : ""
            }`}
            onClick={() =>
              setAnswers({ ...answers, employeeSize: option.value })
            }
          >
            {option.label}
          </div>
        ))}
      </div>
    </Box>
  );

      case 1:
        return (
          <Box className="section">
            <Typography variant="h5" gutterBottom>
              Workforce Structure
            </Typography>

            <FormLabel>
              What types of workers do you pay?
            </FormLabel>

            <FormControlLabel
              control={<Checkbox onChange={() => handleWorkerType("Fixed Monthly Employees", 1)} />}
              label="Fixed Monthly Employees"
            />
            <FormControlLabel
              control={<Checkbox onChange={() => handleWorkerType("Daily Wage Employees", 2)} />}
              label="Daily Wage Employees"
            />
            <FormControlLabel
              control={<Checkbox onChange={() => handleWorkerType("Project-Based Employees", 2)} />}
              label="Project-Based Employees"
            />
            <FormControlLabel
              control={<Checkbox onChange={() => handleWorkerType("Field / Remote Employees", 3)} />}
              label="Field / Remote Employees"
            />
            <FormControlLabel
              control={<Checkbox onChange={() => handleWorkerType("Mixed Compensation Staff", 3)} />}
              label="Mixed Compensation Staff"
            />
          </Box>
        );

      case 2:
        return (
          <Box textAlign="center">
            <Typography variant="h5" gutterBottom>
              Ready to See Your Plan?
            </Typography>

            <Button
              variant="contained"
              color="primary"
              size="large"
              onClick={handleSubmit}
            >
              Get My Recommendation
            </Button>
          </Box>
        );

      case 3:
        return (
          <Box className="result-card">
            <Typography variant="h4" gutterBottom>
              {result.recommendation.plan}
            </Typography>

            <Typography variant="h6">
              {result.recommendation.price}
            </Typography>

            <Typography style={{ marginTop: "1rem" }}>
              Based on your responses, this plan best fits your company’s payroll needs.
            </Typography>

            <Button
              variant="contained"
              color="secondary"
              style={{ marginTop: "1.5rem" }}
            >
              Proceed to Checkout
            </Button>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <div className="finder-wrapper">
      <div className="finder-card">

        <Typography variant="h4" gutterBottom>
          UlapPayroll Plan Recommendation Survey
        </Typography>

        {step < TOTAL_STEPS && (
          <Typography className="progress">
            Step {step + 1} of {TOTAL_STEPS}
          </Typography>
        )}

        {renderStep()}

        {step < TOTAL_STEPS && (
          <div className="navigation-buttons">
            <Button disabled={step === 0} onClick={prevStep}>
              Back
            </Button>

            <Button
              variant="contained"
              color="primary"
              onClick={nextStep}
            >
              Next
            </Button>
          </div>
        )}

      </div>
    </div>
  );
};

export default ProductFinder;
