import React, { useState } from "react";
import {
  Container,
  Typography,
  Box,
  Button,
  Radio,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Checkbox
} from "@material-ui/core";

const ProductFinder = () => {
  const [employeeSize, setEmployeeSize] = useState("");
  const [score, setScore] = useState(null);
  const [result, setResult] = useState(null);

  const calculateScore = () => {
    let total = 0;

    // Company Size Scoring
    if (employeeSize === "1-20") total += 1;
    if (employeeSize === "21-50") total += 2;
    if (employeeSize === "50-150") total += 3;
    if (employeeSize === "151+") total += 4;

    setScore(total);

    // Recommendation Logic
    if (total >= 1 && total <= 10) {
      setResult({ plan: "Starter", price: "₱2,000 / month" });
    } else if (total >= 11 && total <= 20) {
      setResult({ plan: "Professional", price: "₱6,000 / month" });
    } else {
      setResult({ plan: "Enterprise", price: "₱13,000 / month" });
    }
  };

  return (
    <Container maxWidth="md" style={{ marginTop: "3rem" }}>
      <Typography variant="h3" gutterBottom>
        UlapPayroll Product Finder
      </Typography>

      <Box mt={4}>
        <FormControl component="fieldset">
          <FormLabel component="legend">
            How many employees do you have?
          </FormLabel>
          <RadioGroup
            value={employeeSize}
            onChange={(e) => setEmployeeSize(e.target.value)}
          >
            <FormControlLabel value="1-20" control={<Radio />} label="1-20" />
            <FormControlLabel value="21-50" control={<Radio />} label="21-50" />
            <FormControlLabel value="50-150" control={<Radio />} label="50-150" />
            <FormControlLabel value="151+" control={<Radio />} label="151+" />
          </RadioGroup>
        </FormControl>
      </Box>

      <Box mt={4}>
        <Button
          variant="contained"
          color="primary"
          onClick={calculateScore}
        >
          Get Recommendation
        </Button>
      </Box>

      {result && (
        <Box mt={4}>
          <Typography variant="h4">
            Recommended Plan: {result.plan}
          </Typography>
          <Typography variant="h6">
            Price: {result.price}
          </Typography>
        </Box>
      )}
    </Container>
  );
};

export default ProductFinder;
