export const calculatePlan = (answers) => {
  let total = 0;

  // SECTION 1
  total += answers.employeeSize || 0;
  total += answers.branches || 0;
  total += answers.payrollSystem || 0;

  // SECTION 2 - Worker Types
  let complexCount = 0;

  answers.workerTypes.forEach(type => {
    total += type.score;

    if (
      type.label === "Daily Wage Employees" ||
      type.label === "Project-Based Employees" ||
      type.label === "Field / Remote Employees" ||
      type.label === "Mixed Compensation Staff"
    ) {
      complexCount++;
    }
  });

  // Complexity bonus
  if (complexCount >= 3) {
    total += 2;
  }

  // SECTION 3
  total += answers.features || 0;
  total += answers.reports || 0;

  // SECTION 4
  total += answers.approvals || 0;
  total += answers.selfService || 0;

  // SECTION 5
  total += answers.support || 0;

  let recommendation = {};

  if (total >= 1 && total <= 10) {
    recommendation = {
      plan: "ðŸŸ¢ Starter",
      price: "â‚±2,000 / month",
    };
  } else if (total >= 11 && total <= 20) {
    recommendation = {
      plan: "ðŸ”µ Professional",
      price: "â‚±6,000 / month",
    };
  } else {
    recommendation = {
