import React, { useState } from "react";

const PlanFinder = () => {
  const [step, setStep] = useState(1);

  const nextStep = () => {
    setStep(prev => prev + 1);
  };

  const prevStep = () => {
    setStep(prev => prev - 1);
  };

  const renderStep = () => {
    switch (step) {
      case 1:
        return (
          <>
            <h2>How many employees do you have?</h2>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 2:
        return (
          <>
            <h2>Do you operate multiple branches?</h2>
            <button onClick={prevStep}>Back</button>
            <button onClick={nextStep}>Next</button>
          </>
        );

      case 3:
        return (
          <>
            <h2>Finish</h2>
            <button onClick={prevStep}>Back</button>
          </>
        );

      default:
        return null;
    }
  };

  return (
    <div style={{ padding: "2rem" }}>
      <h1>UlapPayroll Plan Finder</h1>
      <p>Step {step}</p>
      {renderStep()}
    </div>
  );
};

export default PlanFinder;
