/**
 * 13th month pay calculation (Philippines).
 * Formula: 13th Month Pay = Total Basic Salary Earned in Calendar Year ÷ 12
 *
 * Total Basic Salary Earned = (Monthly Basic Salary × Months Worked) + Allowances (optional)
 */

/**
 * Compute total salary earned for the year.
 * @param {number} monthlyBasicSalary - Monthly basic salary
 * @param {number} monthsWorked - Months worked in the year (1–12)
 * @param {number} [allowances=0] - Optional allowances to include in total
 * @returns {number}
 */
export function computeTotalSalaryEarned(monthlyBasicSalary, monthsWorked, allowances = 0) {
  if (!monthlyBasicSalary || monthlyBasicSalary <= 0 || !monthsWorked || monthsWorked <= 0) {
    return 0;
  }
  const months = Math.min(12, Math.max(1, Math.round(monthsWorked)));
  return monthlyBasicSalary * months + (Number(allowances) || 0);
}

/**
 * Compute 13th month pay.
 * 13th Month Pay = Total Salary Earned ÷ 12
 * @param {number} totalSalaryEarned - Total basic salary (+ allowances if any) earned in the year
 * @returns {number}
 */
export function compute13thMonthPay(totalSalaryEarned) {
  return totalSalaryEarned / 12;
}

