import React, { useState } from "react";
import { Typography, Box, Button } from "@material-ui/core";
import "./ProductFinder.css";
import { calculatePlan } from "./scoring";

/* =========================
   STEP CONSTANTS
========================= */
const TOTAL_QUESTION_STEPS = 9;
const RESULT_STEP = 9;
const CONTACT_STEP = 10;
const THANKYOU_STEP = 11;
const PLAN_COMPARE_STEP = 12;

const ProductFinder = () => {
  const [step, setStep] = useState(0);
  const [previousStep, setPreviousStep] = useState(null);

  const [answers, setAnswers] = useState({
    employeeSize: 0,
    branches: 0,
    payrollSystem: 0,
    workerTypes: [],
    features: 0,
    reports: 0,
    approvals: 0,
    selfService: 0,
    support: 0,
  });

  const [result, setResult] = useState(null);

  const [contact, setContact] = useState({
    companyName: "",
    fullName: "",
    email: "",
    phone: "",
  });

  /* =========================
     NAVIGATION
  ========================= */
  const nextStep = () => {
    if (step === TOTAL_QUESTION_STEPS - 1) {
      const outcome = calculatePlan(answers);
      setResult(outcome);
      setStep(RESULT_STEP);
    } else if (step < TOTAL_QUESTION_STEPS - 1) {
      setStep(step + 1);
    }
  };

  const prevStep = () => {
    if (step > 0) setStep(step - 1);
  };

  const resetAll = () => {
    setStep(0);
    setPreviousStep(null);
    setAnswers({
      employeeSize: 0,
      branches: 0,
      payrollSystem: 0,
      workerTypes: [],
      features: 0,
      reports: 0,
      approvals: 0,
      selfService: 0,
      support: 0,
    });
    setResult(null);
    setContact({
      companyName: "",
      fullName: "",
      email: "",
      phone: "",
    });
  };

  /* =========================
     VALIDATION
  ========================= */
  const isStepValid = () => {
    switch (step) {
      case 0:
        return answers.employeeSize !== 0;
      case 2:
        return answers.payrollSystem !== 0;
      case 3:
        return answers.workerTypes.length > 0;
      case 4:
        return answers.features !== 0;
      case 5:
        return answers.reports !== 0;
      case 8:
        return answers.support !== 0;
      default:
        return true;
    }
  };

  /* =========================
     WORKER TYPE TOGGLE
  ========================= */
  const handleWorkerType = (label, score) => {
    const exists = answers.workerTypes.find((w) => w.label === label);

    if (exists) {
      setAnswers({
        ...answers,
        workerTypes: answers.workerTypes.filter(
          (w) => w.label !== label
        ),
      });
    } else {
      setAnswers({
        ...answers,
        workerTypes: [...answers.workerTypes, { label, score }],
      });
    }
  };

  /* =========================
     COMPONENTS
  ========================= */

  const SurveyCard = ({ title, field, options }) => (
    <Box className="section">
      <Typography variant="h5">{title}</Typography>
      <div className="option-grid">
        {options.map((option) => (
          <div
            key={option.label}
            className={`option-card ${
              answers[field] === option.value ? "selected" : ""
            }`}
            onClick={() =>
              setAnswers({ ...answers, [field]: option.value })
            }
          >
            {option.label}
          </div>
        ))}
      </div>
    </Box>
  );

  const MultiSurveyCard = ({ title, options }) => (
    <Box className="section">
      <Typography variant="h5">{title}</Typography>
      <div className="option-grid">
        {options.map((option) => {
          const isSelected = answers.workerTypes.some(
            (w) => w.label === option.label
          );

          return (
            <div
              key={option.label}
              className={`option-card ${
                isSelected ? "selected" : ""
              }`}
              onClick={() =>
                handleWorkerType(option.label, option.score)
              }
            >
              {option.label}
            </div>
          );
        })}
      </div>
    </Box>
  );

  const ResultCard = ({ result }) => (
    <Box className="result-card">
      <Typography variant="h4">
        {result?.recommendation?.plan}
      </Typography>

      <Typography variant="h6">
        {result?.recommendation?.price}
      </Typography>

      <Typography style={{ marginTop: "1rem" }}>
        Based on your operational structure and payroll
        complexity, this plan best fits your business.
      </Typography>

      <div className="result-actions">
        <Button
          variant="contained"
          color="secondary"
          className="primary-cta"
          onClick={() => {
            setPreviousStep(RESULT_STEP);
            setStep(CONTACT_STEP);
          }}
        >
          Proceed to Checkout
        </Button>

        <Button
          variant="outlined"
          className="secondary-cta"
          onClick={() => setStep(PLAN_COMPARE_STEP)}
        >
          Seek Another Option
        </Button>
      </div>
    </Box>
  );

  /* =========================
     STEP RENDERER
  ========================= */
  const renderStep = () => {
    switch (step) {

      case 0:
        return (
          <SurveyCard
            title="How many employees do you have?"
            field="employeeSize"
            options={[
              { label: "1–20 Employees", value: 1 },
              { label: "21–50 Employees", value: 2 },
              { label: "50–150 Employees", value: 3 },
              { label: "151+ Employees", value: 4 },
            ]}
          />
        );

      case 1:
        return (
          <SurveyCard
            title="Do you operate multiple branches?"
            field="branches"
            options={[
              { label: "No", value: 0 },
              { label: "2–5 Branches", value: 2 },
              { label: "6+ Branches", value: 3 },
            ]}
          />
        );

      case 2:
        return (
          <SurveyCard
            title="How do you currently process payroll?"
            field="payrollSystem"
            options={[
              { label: "Manually / Spreadsheets", value: 1 },
              { label: "Payroll Software", value: 2 },
              { label: "ERP with Payroll Module", value: 3 },
            ]}
          />
        );

      case 3:
        return (
          <MultiSurveyCard
            title="What types of workers do you pay?"
            options={[
              { label: "Fixed Monthly Employees", score: 1 },
              { label: "Daily Wage Employees", score: 2 },
              { label: "Project-Based Employees", score: 2 },
              { label: "Field / Remote Employees", score: 3 },
              { label: "Mixed Compensation Staff", score: 3 },
            ]}
          />
        );

      case 4:
        return (
          <SurveyCard
            title="What do you want from your payroll & HR solution?"
            field="features"
            options={[
              { label: "Standard features only", value: 1 },
              { label: "Standard + additional tools", value: 2 },
              { label: "Everything available", value: 3 },
            ]}
          />
        );

      case 5:
        return (
          <SurveyCard
            title="What kind of payroll reports do you need?"
            field="reports"
            options={[
              { label: "Standard reports", value: 1 },
              { label: "Advanced compliance reports", value: 2 },
              { label: "Management & analytics reports", value: 3 },
            ]}
          />
        );

      case 6:
        return (
          <SurveyCard
            title="Do you need approval workflows?"
            field="approvals"
            options={[
              { label: "No", value: 0 },
              { label: "Yes – simple approval", value: 2 },
              { label: "Yes – multi-level approval", value: 3 },
            ]}
          />
        );

      case 7:
        return (
          <SurveyCard
            title="Do employees need a self-service portal?"
            field="selfService"
            options={[
              { label: "No", value: 0 },
              { label: "Payslip access only", value: 2 },
              { label: "Full self-service access", value: 3 },
            ]}
          />
        );

      case 8:
        return (
          <SurveyCard
            title="What level of payroll support would you like?"
            field="support"
            options={[
              { label: "Priority support", value: 2 },
              { label: "24/7 Product expert support", value: 3 },
            ]}
          />
        );

      case RESULT_STEP:
        return <ResultCard result={result} />;

      case PLAN_COMPARE_STEP:
        return (
          <div>
            <button
              className="compare-back"
              onClick={() => setStep(RESULT_STEP)}
            >
              ← Back to Recommendation
            </button>

            <div className="compare-layout">
              {["Starter", "Professional", "Enterprise"].map(
                (plan, i) => (
                  <div
                    key={i}
                    className={`compare-card ${plan.toLowerCase()}`}
                  >
                    <h3>{plan}</h3>
                    <p className="compare-price">
                      {plan === "Starter"
                        ? "₱2,000 / month"
                        : plan === "Professional"
                        ? "₱6,000 / month"
                        : "₱13,000 / month"}
                    </p>

                    <Button
                      variant="contained"
                      onClick={() => {
                        setResult({
                          recommendation: {
                            plan,
                            price:
                              plan === "Starter"
                                ? "₱2,000 / month"
                                : plan === "Professional"
                                ? "₱6,000 / month"
                                : "₱13,000 / month",
                          },
                        });
                        setPreviousStep(PLAN_COMPARE_STEP);
                        setStep(CONTACT_STEP);
                      }}
                    >
                      Choose {plan}
                    </Button>
                  </div>
                )
              )}
            </div>
          </div>
        );

      case CONTACT_STEP:
  return (
    <div className="contact-wrapper">
      
      <button
        className="contact-back"
        onClick={() => setStep(RESULT_STEP)}
      >
        ← Back
      </button>

      <div className="contact-content">
        <Typography variant="h5" className="contact-title">
          Company Details
        </Typography>

        <div className="contact-grid">
          {[
            { key: "companyName", label: "Company Name" },
            { key: "fullName", label: "Full Name" },
            { key: "email", label: "Email Address" },
            { key: "phone", label: "Phone Number" },
          ].map((field) => (
            <div className="input-group" key={field.key}>
              <input
                type="text"
                placeholder=" "
                value={contact[field.key]}
                onChange={(e) =>
                  setContact({
                    ...contact,
                    [field.key]: e.target.value,
                  })
                }
              />
              <label>{field.label}</label>
            </div>
          ))}
        </div>

        <button
          className="complete-btn"
          disabled={
            !contact.companyName ||
            !contact.fullName ||
            !contact.email ||
            !contact.phone
          }
          onClick={() => setStep(THANKYOU_STEP)}
        >
          Complete Setup →
        </button>
      </div>
    </div>
  );

      case THANKYOU_STEP:
  return (
    <div className="confirmation-wrapper">

      <div className="confirmation-layout">

        {/* LEFT PANEL */}
        <div className="confirmation-summary">
          <h3>Purchase Summary</h3>

          <div className="summary-item">
            <span>Selected Plan</span>
            <strong>{result?.recommendation?.plan}</strong>
          </div>

          <div className="summary-item">
            <span>Monthly Price</span>
            <strong>{result?.recommendation?.price}</strong>
          </div>

          <div className="summary-divider" />

          <div className="summary-total">
            <span>Total</span>
            <strong>{result?.recommendation?.price}</strong>
          </div>

          <div className="summary-total">
            <span>Maintenance</span>
            <strong>{result?.recommendation?.price}</strong>
          </div>
          
          <div className="summary-total">
            <span>Total</span>
            <strong>{result?.recommendation?.price}</strong>
          </div>
          
        </div>

        {/* RIGHT PANEL */}
        <div className="confirmation-details">
          <h2>Payment Confirmed</h2>
          <p className="confirmation-sub">
            Thank you for choosing UlapPayroll.
          </p>

          <div className="confirmation-box">
            <p>
              ✔ A confirmation email has been sent to:
            </p>
            <strong>{contact.email}</strong>

            <p style={{ marginTop: "1rem" }}>
              Our onboarding team will contact you shortly to
              begin your system setup.
            </p>
          </div>

          <button
            className="complete-btn"
            onClick={resetAll}
            style={{ marginTop: "2rem" }}
          >
            Start Again
          </button>
        </div>

      </div>
    </div>
  );

      default:
        return null;
    }
  };

  return (
    <div className="finder-wrapper">
      <div className="finder-card">

        {step < TOTAL_QUESTION_STEPS && (
          <div className="progress-container">
            <div className="progress-bar">
              <div
                className="progress-fill"
                style={{
                  width: `${
                    ((step + 1) /
                      TOTAL_QUESTION_STEPS) *
                    100
                  }%`,
                }}
              />
            </div>
            <Typography>
              Step {step + 1} of {TOTAL_QUESTION_STEPS}
            </Typography>
          </div>
        )}

        {renderStep()}

        {step < TOTAL_QUESTION_STEPS && (
          <div className="navigation-buttons">
            <Button
              disabled={step === 0}
              onClick={prevStep}
            >
              Back
            </Button>

            <Button
              variant="contained"
              color="primary"
              onClick={nextStep}
              disabled={!isStepValid()}
            >
              Next
            </Button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductFinder;
